pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

contract PasscodeAirdrop is Ownable {
    bytes32 public merkleRoot;
    address public tokenContract;
    mapping(string => bool) public redeemedList;
    uint256 public start;
    uint256 public end;
    address public signer;
    bytes16 private constant _SYMBOLS = "0123456789abcdef";

    constructor(address _tokenContract, uint256 _start, uint256 _end, address _signer) {
        tokenContract = _tokenContract;
        start = _start;
        end = _end;
        signer = _signer;
    }

    function changeMerkleRoot(bytes32 _merkleRoot) public onlyOwner {
        merkleRoot = _merkleRoot;
    }

    function changeTime(uint256 _start, uint256 _end) public onlyOwner {
        start = _start;
        end = _end;
    }

    function changeSigner(address _signer) public onlyOwner {
        signer = _signer;
    }

    function recoverSigner(bytes32 message, bytes memory _sig) public pure returns (address) {
        uint8 v;
        bytes32 r;
        bytes32 s;
        assembly {
            // next 32 bytes, after the length prefix
            r := mload(add(_sig, 32))
            // next 32 bytes after r
            s := mload(add(_sig, 64))
            // final byte (first byte of the next 32 bytes) - last byte in the signature, mload picks 32 bytes at a time, so we need to pick just the first 2
            v := byte(0, mload(add(_sig, 96)))
        }
        return ecrecover(message, v, r, s);
    }

    function strConcat(string memory _a, string memory _b) public pure returns (string memory){
        bytes memory _ba = bytes(_a);
        bytes memory _bb = bytes(_b);
        string memory ret = new string(_ba.length + _bb.length);
        bytes memory bret = bytes(ret);
        uint k = 0;
        for (uint i = 0; i < _ba.length; i++) bret[k++] = _ba[i];
        for (uint i = 0; i < _bb.length; i++) bret[k++] = _bb[i];
        return string(ret);
    }

    function toHexString(uint256 value, uint256 length) internal pure returns (string memory) {
        bytes memory buffer = new bytes(2 * length);
        for (uint256 i = 2 * length; i > 0; --i) {
            buffer[i - 1] = _SYMBOLS[value & 0xf];
            value >>= 4;
        }
        require(value == 0, "Strings: hex length insufficient");
        return string(buffer);
    }

    function redeem(string memory passcode, uint256 amount, bytes memory sig, bytes32[] memory proof) public {
        require(merkleRoot.length > 0, "error: 10005 merkle root not set");
        require(block.timestamp >= start && block.timestamp <= end, "error: 10008 redeem time not allowed");
        require(!redeemedList[passcode], "error: 10010 already redeemed");
        require(MerkleProof.verify(proof, merkleRoot, keccak256(abi.encodePacked(passcode, amount))), "error: 10007 invalid proof");
        require(IERC20(tokenContract).balanceOf(address(this)) >= amount, "error: 10009 redeem token insufficient");
        require(recoverSigner(keccak256(abi.encodePacked(strConcat(string("\x19Ethereum Signed Message:\n64"), toHexString(uint256(keccak256(abi.encodePacked(address(this), passcode, amount, msg.sender))), 32)))), sig) == signer, "error: 10011 invalid signature");
        redeemedList[passcode] = true;
        (bool success, bytes memory data) = tokenContract.call(abi.encodeWithSelector(0xa9059cbb, _msgSender(), amount));
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "GG: Failed to withdraw Ether"
        );
    }

    // This allows the contract owner to withdraw the funds from the contract.
    function withdraw(uint amt, address _tokenContract) external onlyOwner {
        if (_tokenContract == address(0)) {
            (bool sent, ) = payable(owner()).call{value: amt}("");
            require(sent, "GG: Failed to withdraw Ether");
        } else {
            (bool success, bytes memory data) = _tokenContract.call(abi.encodeWithSelector(0xa9059cbb, owner(), amt));
            require(
                success && (data.length == 0 || abi.decode(data, (bool))),
                "GG: Failed to withdraw Ether"
            );
        }
    }
}
